/***********************************//******** HTML Wireframe JS ********//***********************************/$(document).ready(function(){$("[data-description]").each(function(){$(this).html($(this).html()+'<div class="wf-description">'+$(this).data("description")+"</div>");$(this).css("position","relative")});$(".wf-description").hover(function(){$(this).addClass("view-description")},function(){$(this).removeClass("view-description")})});